// src/VideoCall.tsx
import React, { useEffect, useRef, useState } from 'react';
import './VideoCall.css'; // optional, for styling

const VideoCall: React.FC = () => {
  // Role determined by URL parameter: ?caller=1 means caller, otherwise callee.
  const params = new URLSearchParams(window.location.search);
  const isCaller = params.get('caller') === '1';

  const localVideoRef = useRef<HTMLVideoElement | null>(null);
  const remoteVideoRef = useRef<HTMLVideoElement | null>(null);
  const [peerConnection, setPeerConnection] = useState<RTCPeerConnection | null>(null);
  const ws = useRef<WebSocket | null>(null);

  useEffect(() => {
    // 1. Create RTCPeerConnection with a public STUN server
    const pc = new RTCPeerConnection({
      iceServers: [{ urls: 'stun:stun.l.google.com:19302' }],
    });
    setPeerConnection(pc);

    // 2. Handle remote stream
    pc.ontrack = (event) => {
      if (remoteVideoRef.current) {
        remoteVideoRef.current.srcObject = event.streams[0];
      }
    };

    // 3. Connect to the WebSocket signaling server
    ws.current = new WebSocket('ws://localhost:8080');

    ws.current.onmessage = async (event) => {
      // Ensure we only parse text messages.
      if (typeof event.data !== 'string') {
        console.warn('Received non-text message, ignoring:', event.data);
        return;
      }
      let data;
      try {
        data = JSON.parse(event.data);
      } catch (e) {
        console.error('Failed to parse JSON:', event.data, e);
        return;
      }

      // NEW: Handle new_peer notification to trigger renegotiation
      if (data.new_peer) {
        console.log('New peer connected, triggering renegotiation...');
        if (isCaller && pc.signalingState === 'stable') {
          try {
            const offer = await pc.createOffer();
            await pc.setLocalDescription(offer);
            ws.current?.send(JSON.stringify({ offer }));
            console.log('Renegotiation offer sent:', offer);
          } catch (error) {
            console.error('Error during renegotiation:', error);
          }
        }
        return;
      }

      if (data.offer) {
        console.log('Got offer:', data.offer);
        if (pc.signalingState !== 'stable') {
          console.warn('Received offer while not stable.');
          return;
        }
        await pc.setRemoteDescription(new RTCSessionDescription(data.offer));
        const answer = await pc.createAnswer();
        await pc.setLocalDescription(answer);
        ws.current?.send(JSON.stringify({ answer }));
      } else if (data.answer) {
        console.log('Got answer:', data.answer);
        await pc.setRemoteDescription(new RTCSessionDescription(data.answer));
      } else if (data.ice) {
        try {
          await pc.addIceCandidate(data.ice);
        } catch (err) {
          console.error('Error adding ICE candidate', err);
        }
      }
    };

    // 4. Send ICE candidates as they are gathered.
    pc.onicecandidate = (event) => {
      if (event.candidate) {
        ws.current?.send(JSON.stringify({ ice: event.candidate }));
      }
    };

    // 5. Get local camera stream and add tracks to the RTCPeerConnection.
    navigator.mediaDevices.getUserMedia({ video: true })
      .then((stream) => {
        if (localVideoRef.current) {
          localVideoRef.current.srcObject = stream;
        }
        stream.getTracks().forEach((track) => {
          pc.addTrack(track, stream);
        });
      })
      .catch((error) => {
        console.error('Error accessing camera:', error);
      });

    // 6. On WebSocket open, if this peer is caller, send an offer.
    ws.current.onopen = () => {
      console.log('WebSocket onopen! isCaller =', isCaller);
      if (isCaller) {
        // Delay slightly to reduce collisions.
        setTimeout(async () => {
          if (pc.signalingState === 'stable') {
            try {
              const offer = await pc.createOffer();
              await pc.setLocalDescription(offer);
              ws.current?.send(JSON.stringify({ offer }));
              console.log('Offer sent:', offer);
            } catch (error) {
              console.error('Error creating offer:', error);
            }
          }
        }, Math.random() * 500);
      }
    };

    // Cleanup on unmount.
    return () => {
      pc.close();
      ws.current?.close();
      console.log('WebSocket onclose!');
    };
  }, [isCaller]);

  return (
    <div className="video-call-container">
      <div className="header">
        <h2>Two-Tab Video Call Demo</h2>
        <p>
          <strong>Role:</strong> {isCaller ? 'Caller' : 'Callee'}
        </p>
      </div>
      <div className="video-container">
        <div className="video-box">
          <h3>Local Video</h3>
          <video ref={localVideoRef} autoPlay playsInline muted />
        </div>
        <div className="video-box">
          <h3>Remote Video</h3>
          <video ref={remoteVideoRef} autoPlay playsInline />
        </div>
      </div>
    </div>
  );
};

export default VideoCall;
