// src/VideoCall.tsx
import React, { useEffect, useRef, useState } from 'react';
import './VideoCall.css';

const VideoCall: React.FC = () => {
  // Determine role (caller/callee) from URL param: ?caller=1
  const params = new URLSearchParams(window.location.search);
  const isCaller = params.get('caller') === '1';

  // Refs for local/remote videos
  const localVideoRef = useRef<HTMLVideoElement | null>(null);
  const remoteVideoRef = useRef<HTMLVideoElement | null>(null);

  // Store the peer connection and WebSocket
  const [peerConnection, setPeerConnection] = useState<RTCPeerConnection | null>(null);
  const ws = useRef<WebSocket | null>(null);

  // Track if we've started local camera
  const [localStreamStarted, setLocalStreamStarted] = useState(false);
  // Track if we're currently "connected"
  const [isConnected, setIsConnected] = useState(false);

  useEffect(() => {
    // 1) Create RTCPeerConnection
    const pc = new RTCPeerConnection({
      iceServers: [{ urls: 'stun:stun.l.google.com:19302' }],
    });
    setPeerConnection(pc);

    // 2) On remote track
    pc.ontrack = (event) => {
      if (remoteVideoRef.current) {
        remoteVideoRef.current.srcObject = event.streams[0];
      }
    };

    // 3) Connect to signaling server
    ws.current = new WebSocket('ws://localhost:8080');

    ws.current.onmessage = async (event) => {
      if (typeof event.data !== 'string') {
        console.warn('Ignoring non-text message');
        return;
      }
      let data;
      try {
        data = JSON.parse(event.data);
      } catch (e) {
        console.error('Failed to parse message', event.data, e);
        return;
      }

      if (data.offer) {
        console.log('Got offer:', data.offer);
        if (pc.signalingState !== 'stable') {
          console.warn('Received offer while not stable.');
          return;
        }
        await pc.setRemoteDescription(new RTCSessionDescription(data.offer));
        const answer = await pc.createAnswer();
        await pc.setLocalDescription(answer);
        ws.current?.send(JSON.stringify({ answer }));
      } else if (data.answer) {
        console.log('Got answer:', data.answer);
        await pc.setRemoteDescription(new RTCSessionDescription(data.answer));
      } else if (data.ice) {
        try {
          await pc.addIceCandidate(data.ice);
        } catch (err) {
          console.error('Error adding ICE candidate', err);
        }
      }
    };

    // 4) Send ICE candidates
    pc.onicecandidate = (event) => {
      if (event.candidate) {
        ws.current?.send(JSON.stringify({ ice: event.candidate }));
      }
    };

    return () => {
      pc.close();
      ws.current?.close();
    };
  }, []);

  // Start local camera if not already
  const startLocalStream = async () => {
    if (localStreamStarted) return;
    try {
      const stream = await navigator.mediaDevices.getUserMedia({ video: true });
      if (localVideoRef.current) {
        localVideoRef.current.srcObject = stream;
      }
      peerConnection?.getSenders().forEach(sender => peerConnection?.removeTrack(sender));
      stream.getTracks().forEach(track => {
        peerConnection?.addTrack(track, stream);
      });
      setLocalStreamStarted(true);
    } catch (err) {
      console.error('Error accessing camera:', err);
    }
  };

  // Handle "Connect" click
  const handleConnect = async () => {
    if (!peerConnection || !ws.current) return;

    // Start local camera
    await startLocalStream();

    // If caller, create and send offer
    if (isCaller) {
      try {
        if (peerConnection.signalingState === 'stable') {
          const offer = await peerConnection.createOffer();
          await peerConnection.setLocalDescription(offer);
          ws.current.send(JSON.stringify({ offer }));
          console.log('Offer sent:', offer);
        }
      } catch (err) {
        console.error('Error creating offer:', err);
      }
    }

    setIsConnected(true);
  };

  // Handle "End Call" click
  const handleEndCall = () => {
    console.log('Ending call...');
    if (peerConnection) {
      peerConnection.close();
    }
    // Reset state
    setIsConnected(false);
    setLocalStreamStarted(false);
    // Clear remote video
    if (remoteVideoRef.current) {
      (remoteVideoRef.current.srcObject as MediaStream | null) = null;
    }
    // Optionally stop local tracks
    if (localVideoRef.current) {
      const stream = localVideoRef.current.srcObject as MediaStream;
      if (stream) {
        stream.getTracks().forEach(track => track.stop());
      }
      localVideoRef.current.srcObject = null;
    }
  };

  return (
    <div className="video-call-container">
      <div className="header">
        <h2>Two-Tab Video Call Demo with ASL Detection</h2>
        <p><strong>Role:</strong> {isCaller ? 'Caller' : 'Callee'}</p>
      </div>

      <div className="button-group">
        {!isConnected && (
          <button className="connect-btn" onClick={handleConnect}>
            {isCaller ? 'Connect as Caller' : 'Connect as Callee'}
          </button>
        )}
        {isConnected && (
          <button className="end-btn" onClick={handleEndCall}>
            End Call
          </button>
        )}
      </div>

      <div className="video-container">
        <div className="video-box">
          <h3>Local Video</h3>
          <video ref={localVideoRef} autoPlay playsInline muted />
        </div>
        <div className="video-box">
          <h3>Remote Video</h3>
          <video ref={remoteVideoRef} autoPlay playsInline />
        </div>
      </div>
    </div>
  );
};

export default VideoCall;
