// src/VideoCall.tsx
import React, { useEffect, useRef, useState } from 'react';

const VideoCall: React.FC = () => {
  const localVideoRef = useRef<HTMLVideoElement | null>(null);
  const remoteVideoRef = useRef<HTMLVideoElement | null>(null);
  const [peerConnection, setPeerConnection] = useState<RTCPeerConnection | null>(null);
  const ws = useRef<WebSocket | null>(null);

  useEffect(() => {
    // Configure STUN server (using Google’s public STUN server)
    const configuration = {
      iceServers: [{ urls: 'stun:stun.l.google.com:19302' }]
    };

    // Create RTCPeerConnection
    const pc = new RTCPeerConnection(configuration);
    setPeerConnection(pc);

    // When a remote stream arrives, attach it to the remoteVideo element
    pc.ontrack = (event) => {
      if (remoteVideoRef.current) {
        remoteVideoRef.current.srcObject = event.streams[0];
      }
    };

    // Connect to the signaling server
    ws.current = new WebSocket('ws://localhost:8080');
      
    ws.current.onmessage = async (messageEvent) => {
      const data = JSON.parse(messageEvent.data);
      if (data.offer) {
        // Received an offer from a remote peer
        await pc.setRemoteDescription(new RTCSessionDescription(data.offer));
        const answer = await pc.createAnswer();
        await pc.setLocalDescription(answer);
        ws.current?.send(JSON.stringify({ answer }));
      } else if (data.answer) {
        // Received answer to our offer
        await pc.setRemoteDescription(new RTCSessionDescription(data.answer));
      } else if (data.ice) {
        // Received ICE candidate from remote peer
        try {
          await pc.addIceCandidate(data.ice);
        } catch (e) {
          console.error('Error adding ICE candidate', e);
        }
      }
    };

    // When a new ICE candidate is found, send it via signaling
    pc.onicecandidate = (event) => {
      if (event.candidate) {
        ws.current?.send(JSON.stringify({ ice: event.candidate }));
      }
    };

    // Get the local media stream and add it to the peer connection
    // Get the local media stream and add it to the peer connection
navigator.mediaDevices.getUserMedia({ video: true, audio: true })
.then((stream) => {
  if (localVideoRef.current) {
    localVideoRef.current.srcObject = stream;
  }
  stream.getTracks().forEach((track) => {
    pc.addTrack(track, stream);
  });
})
.catch((error) => {
  console.error('Error accessing media devices:', error);
});


    // Once signaling connection is open, create an offer
    ws.current.onopen = async () => {
      try {
        const offer = await pc.createOffer();
        await pc.setLocalDescription(offer);
        ws.current?.send(JSON.stringify({ offer }));
      } catch (error) {
        console.error('Error creating offer:', error);
      }
    };

    // Clean up on unmount
    return () => {
      pc.close();
      ws.current?.close();
    };
  }, []);

  return (
    <div>
      <h2>Video Call</h2>
      <div style={{ display: 'flex', gap: '1rem' }}>
        <div>
          <h3>Local Video</h3>
          <video ref={localVideoRef} autoPlay playsInline muted style={{ width: '300px' }} />
        </div>
        <div>
          <h3>Remote Video</h3>
          <video ref={remoteVideoRef} autoPlay playsInline style={{ width: '300px' }} />
        </div>
      </div>
    </div>
  );
};

export default VideoCall;
